package StringExamples;

public class StringReplace {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		String str = "Hello World";
		System.out.println(str.replace('H', 'R'));
		String str1 = "Pijuli pijuli";
		System.out.println(str1.replaceAll("Pi", "Tu"));
		String str2= "Kakharu Kakharu";
		System.out.println(str2.replaceFirst("ak", "oo"));
		
	}

}
